import ToDoList from "../ToDoList/ToDoList";
import { useState } from 'react'
import context from "../../context";

function App() {

  const List = [
    {id: 1, title: 'поесть', done: 1},
    {id: 2, title: 'позаниматься', done: 1},
    {id: 3, title: 'погулять', done: 0},
    {id: 4, title: 'встретиться с друзьями', done: 1},
    {id: 5, title: 'поспать', done: 0}
  ];

  const [todoList, setTodoList] = useState(List);

  const StatusDone = (id) => {
    const newState = todoList.map(el => {
        if(el.id === id){
        el.done = el.done === 0 ? 1 : 0;
        }
        return el
    })
    setTodoList(newState);
}

  return (
    <div className="App">
      <context.Provider >
        {
            todoList.map(el => <ToDoList {...el} key={el.id} {...{StatusDone}}/>)
        }
      </context.Provider>
    </div>
  );
}

export default App;
