import React from 'react'

export default function AddCardForm({addWords}) {

    const onSubmit = (event) =>{
        event.preventDefault();
        const rus = event.target;
        const eng = event.target.eng.value;
        const newWords = {
            id: Date.now(),
            rus, eng
        }
        addWords(newWords);
        event.target.reset();
    }
  return (
    <div onSubmit={onSubmit}>
        <input type="title" placeholder='Русское слово' name='rus'/>
        <input type="title" placeholder='Английское слово' name='eng'/>
        <button>добавить</button>
    </div>
  )
}
