import React from 'react'
import s from './stylePost.module.css'

export default function Post({id, title, description, like, switch_like}) {

    const like_text = like === false ? 'Liked' : 'like ?'

    const like_style = {
        backgroundColor: like ? 'blue' : 'azure',
        color: like ? 'white' : 'black'
      }
    
  return (
    <div className={s.item}>
        <p>{title}</p>
        <p>{description}</p>
        <button style={like_style} onClick={() => switch_like(id)}>{like_text}</button>
    </div>
  )
}
