import { useState } from 'react'
import { posts } from '../src/data/posts.js'
import PostContainer from './components/PostContainer/PostContainer.jsx';
import { Context } from './contex.js';

function App() {

  const [cardsPosts, setCardsPosts] = useState(posts);

  const switch_like = id => {
    const target_post = posts.find(el => el.id === id);
    target_post.like = !target_post.like;
    setCardsPosts([...posts]);
  }


  return (
    <div className="App">
      <Context.Provider value={{cardsPosts}}>
        <PostContainer {...{cardsPosts}}/>
      </Context.Provider>
    </div>
  );
}

export default App;
